package org.stjohn.dayone;

public class DatatypesDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//integer will give us the values in whole number
		//float and double will give us the value in decimal point
		/*if we use in programming division it prints only quotients value and modulo will print % prints 
		 remainder value */
		
		int num1=5/2;
		float num2=103/45;
		//if we use d after any number for 
		//double type it will give the precision upto 16 decimal places
		double num3=10d/3d;
		//if we use f after any number for 
		//double type it will give the precision upto 7 decimal places
		float num4=14f/3f;
		System.out.println("the value 1 is:"+num1);
		System.out.println("the value 2 is:"+num2);
		System.out.println("the value 3 is:"+num3);
		System.out.println("the value 4 is:"+num4);

	}

}
